function h5show(src) {
    let h5 = document.getElementById('h5')
    let ifra = document.getElementById('ifra')
    ifra.src = src
    h5.style.display = "block";
}
function h5hide(e) {
    let h5 = document.getElementById('h5')
    h5.style.display = "none";
    let ifra = document.getElementById('ifra')
    ifra.src = "https://cocoto.gitee.io/wiki/assets/img/favicon.ico"
}
function boxShow(e) {
    let t = document.getElementById('PBOX')
    t.style.display = "block";
    document.onmousemove = function (ev) {
        let oEvent = ev || event;
        let box = document.getElementById('cube');
        x = oEvent.clientX / (window.screen.availWidth / 36);
        y = oEvent.clientY / (window.screen.availHeight / 36);
        box.style.cssText = 'transform:rotateY(' + 10 * x + 'deg) rotateX(' + 10 * y + 'deg);';
    }
}
function boxHide(e) {
    let t = document.getElementById('PBOX')
    t.style.display = "none";
    document.onmousemove = null
}
